type ChallengeAcceptedLiveTeaserProps = {
  Link: React.ElementType
}

export function ChallengeAcceptedLiveTeaser({
  Link,
}: ChallengeAcceptedLiveTeaserProps) {
  return (
    <Link href='/challenge-accepted'>
      <a
        {...plainLinkRule}
        {...css({
          backgroundColor: '#EBEA2B',
          color: '#000000',
        })}
        {...styles.container}
      >
        <div {...styles.imageWrapper}>
          <Logo />
        </div>
        <div {...styles.textWrapper}>
          <p {...styles.text}>
            25 Menschen, die in der Klimakrise einen Unterschied machen.
            Gemeinsam gehen wir der Frage nach: Wie kommen wir aus dieser Krise
            wieder raus? Mit laufend neuen{' '}
            <span {...styles.underline}>
              Artikeln, Debatten, Veranstaltungen
            </span>
            .
          </p>
        </div>
      </a>
    </Link>
  )
}

